}

resample &lt;- function(df){
  if (!is.data.frame(df)){
    stop("The df object must be a data frame.")
  }
  dplyr::slice_sample(df, n = nrow(df), replace = T )
}


penguins &lt;- penguins |&gt; drop_na()

Poisson Distribution


Code
finder &lt;- function(u){
  x &lt;- 0
  condition &lt;- TRUE
  while (condition) {
    uu &lt;- ppois(x, lambda = 6)
    condition &lt;- uu &lt;= u
    if(condition){
      x &lt;- x + 1
    }
  }
  return(x)
}
xx &lt;- sapply(u, finder)  
xx |&gt; tibble(x = _) |&gt; 
ggplot(aes(x=x, y = ..density..)) +
geom_histogram(bins = 21) +
geom_step(data = tibble(x = xe, y = dpois(xe, lambda = 6)),
          mapping = aes(x,y)) +
theme_bw()

Cauchy Distribution


Code
norm &lt;- function(x){
